import React from 'react';

function Header({ tokenPrice, priceChange24h, loading, range, onRangeChange }) {
  return (
    <header className="header">
      <h1>$PUFBoard</h1>
      <div className="token-info">
        <div className="token-price">
          <span className="price-label">Current Price:</span>
          <span
            className={`price-value ${loading ? 'loading' : ''}`}
            style={tokenPrice.includes('Error') ? { color: '#ff9800' } : {}}
            title={tokenPrice.includes('Error') ? 'Price data temporarily unavailable' : ''}
          >
            {tokenPrice}
          </span>
        </div>
        {priceChange24h !== null && !isNaN(priceChange24h) && (
          <PriceChange change={priceChange24h} />
        )}
      </div>
    </header>
  );
}

function PriceChange({ change }) {
  const changeValue = parseFloat(change);
  const isPositive = changeValue >= 0;

  return (
    <div className="price-change">
      <span className="change-label">24h:</span>
      <span className={`change-value ${isPositive ? 'positive' : 'negative'}`}>
        {isPositive ? '+' : ''}{changeValue.toFixed(2)}%
      </span>
    </div>
  );
}

export default Header;
