import React from 'react';
import DataCard from './DataCard';
import CombinedCard from './CombinedCard';
import TabsCard from './TabsCard';

function Dashboard({
  burnedAmount,
  treasuryAmount,
  royaltiesAmount,
  totalVolume,
  burnedPercentage,
  treasuryPercentage,
  royaltiesPercentage,
  loading,
  lastSync,
  userStats,
  tokenStats,
  uniqueCreators,
  range,
  rangeUsers,
  rangeTokens,
  rangeCreators,
  onRangeChange
}) {
  // Prepare data for Users TabsCard
  const usersData = {
    all: {
      primary: (userStats?.total || 0) + 45000,
      primaryLabel: 'total users',
      secondary: undefined
    },
    '30d': {
      primary: userStats?.monthly || 0,
      primaryLabel: 'new users (30d)',
      secondary: (userStats?.total || 0) + 45000,
      secondaryLabel: 'total users'
    },
    '7d': {
      primary: userStats?.weekly || 0,
      primaryLabel: 'new users (7d)',
      secondary: (userStats?.total || 0) + 45000,
      secondaryLabel: 'total users'
    },
    '24h': {
      primary: userStats?.daily || 0,
      primaryLabel: 'new users (24h)',
      secondary: (userStats?.total || 0) + 45000,
      secondaryLabel: 'total users'
    }
  };

  // Prepare data for Tokens & Creators TabsCard
  const tokensData = {
    all: {
      primary: tokenStats?.total || 0,
      primaryLabel: 'total tokens',
      secondary: uniqueCreators || 0,
      secondaryLabel: 'unique creators'
    },
    '30d': {
      primary: range === '30d' ? rangeTokens : (tokenStats?.monthly || 0),
      primaryLabel: 'new tokens (30d)',
      secondary: range === '30d' ? rangeCreators : 0,
      secondaryLabel: 'unique creators'
    },
    '7d': {
      primary: range === '7d' ? rangeTokens : (tokenStats?.weekly || 0),
      primaryLabel: 'new tokens (7d)',
      secondary: range === '7d' ? rangeCreators : 0,
      secondaryLabel: 'unique creators'
    },
    '24h': {
      primary: range === '24h' ? rangeTokens : (tokenStats?.daily || 0),
      primaryLabel: 'new tokens (24h)',
      secondary: range === '24h' ? rangeCreators : 0,
      secondaryLabel: 'unique creators'
    }
  };

  // Prepare combined platform data
  const platformData = {
    all: {
      primary: (userStats?.total || 0) + 45000,
      primaryLabel: 'total users',
      secondary: tokenStats?.total || 0,
      secondaryLabel: 'total tokens',
      tertiary: uniqueCreators || 0,
      tertiaryLabel: 'unique creators'
    },
    '30d': {
      primary: userStats?.monthly || 0,
      primaryLabel: 'new users (30d)',
      secondary: range === '30d' ? rangeTokens : (tokenStats?.monthly || 0),
      secondaryLabel: 'new tokens (30d)',
      tertiary: range === '30d' ? rangeCreators : 0,
      tertiaryLabel: 'unique creators (30d)'
    },
    '7d': {
      primary: userStats?.weekly || 0,
      primaryLabel: 'new users (7d)',
      secondary: range === '7d' ? rangeTokens : (tokenStats?.weekly || 0),
      secondaryLabel: 'new tokens (7d)',
      tertiary: range === '7d' ? rangeCreators : 0,
      tertiaryLabel: 'unique creators (7d)'
    },
    '24h': {
      primary: userStats?.daily || 0,
      primaryLabel: 'new users (24h)',
      secondary: range === '24h' ? rangeTokens : (tokenStats?.daily || 0),
      secondaryLabel: 'new tokens (24h)',
      tertiary: range === '24h' ? rangeCreators : 0,
      tertiaryLabel: 'unique creators (24h)'
    }
  };

  return (
    <main className="dashboard">
      <TabsCard
        title="Platform Activity"
        data={platformData}
        loading={loading}
        currentRange={range}
        onRangeChange={onRangeChange}
      />

      <CombinedCard
        title="PUF Distribution"
        loading={loading}
        lastSync={lastSync}
        items={[
          {
            value: burnedAmount,
            unit: 'PUF',
            label: 'Burned',
            percentage: burnedPercentage
          },
          {
            value: treasuryAmount,
            unit: 'PUF',
            label: 'Treasury',
            percentage: treasuryPercentage
          },
          {
            value: royaltiesAmount,
            unit: 'WLD',
            label: 'Creator Royalties',
            percentage: royaltiesPercentage,
            totalVolume: totalVolume
          }
        ]}
      />
    </main>
  );
}

export default Dashboard;
