import React from 'react';
import DataCard from './DataCard';
import MetricCard from './MetricCard';

function Dashboard({
  burnedAmount,
  treasuryAmount,
  royaltiesAmount,
  totalVolume,
  burnedPercentage,
  treasuryPercentage,
  royaltiesPercentage,
  loading,
  lastSync,
  userStats,
  tokenStats,
  uniqueCreators,
  range,
  rangeUsers,
  rangeTokens,
  rangeCreators
}) {
  return (
    <main className="dashboard">
      <MetricCard
        title="Users"
        loading={loading}
        rows={[
          { label: 'All', value: userStats?.total || 0 },
          { label: '30d', value: userStats?.monthly || 0 },
          { label: '7d', value: userStats?.weekly || 0 },
          { label: '24h', value: userStats?.daily || 0 }
        ]}
      />

      <MetricCard
        title="Tokens & Real Humans"
        loading={loading}
        rows={[
          { label: `Tokens (${range})`, value: rangeTokens || 0 },
          { label: `Creators (${range})`, value: rangeCreators || 0 },
          { label: 'Tokens (All)', value: tokenStats?.total || 0 },
          { label: 'Creators (All)', value: uniqueCreators || 0 }
        ]}
      />

      <DataCard
        title="Creator Royalties Distributed"
        value={royaltiesAmount}
        unit="WLD"
        percentage={royaltiesPercentage}
        cardType="royalties"
        loading={loading}
        lastSync={lastSync}
        totalVolume={totalVolume}
      />

      <DataCard
        title="Burned Amount"
        value={burnedAmount}
        unit="PUF"
        percentage={burnedPercentage}
        cardType="burned"
        loading={loading}
        lastSync={lastSync}
      />

      <DataCard
        title="Sent to Treasury"
        value={treasuryAmount}
        unit="PUF"
        percentage={treasuryPercentage}
        cardType="treasury"
        loading={loading}
        lastSync={lastSync}
      />
    </main>
  );
}

export default Dashboard;
