import React from 'react';
import DataCard from './DataCard';
import CombinedCard from './CombinedCard';
import StatsCard from './StatsCard';

function Dashboard({
  burnedAmount,
  treasuryAmount,
  royaltiesAmount,
  totalVolume,
  burnedPercentage,
  treasuryPercentage,
  royaltiesPercentage,
  loading,
  lastSync,
  userStats,
  tokenStats,
  uniqueCreators,
  range,
  rangeUsers,
  rangeTokens,
  rangeCreators
}) {
  return (
    <main className="dashboard">
      <StatsCard
        title="Users"
        range={range}
        loading={loading}
        currentData={{
          primary: rangeUsers,
          primaryLabel: 'new users',
          secondary: userStats?.total || 0,
          secondaryLabel: 'total users'
        }}
      />

      <StatsCard
        title="Tokens & Creators"
        range={range}
        loading={loading}
        currentData={{
          primary: rangeTokens,
          primaryLabel: 'new tokens',
          secondary: rangeCreators,
          secondaryLabel: 'unique creators'
        }}
      />

      <CombinedCard
        title="PUF Distribution"
        loading={loading}
        lastSync={lastSync}
        items={[
          {
            value: burnedAmount,
            unit: 'PUF',
            label: 'Burned',
            percentage: burnedPercentage
          },
          {
            value: treasuryAmount,
            unit: 'PUF',
            label: 'Treasury',
            percentage: treasuryPercentage
          }
        ]}
      />

      <DataCard
        title="Creator Royalties Distributed"
        value={royaltiesAmount}
        unit="WLD"
        percentage={royaltiesPercentage}
        cardType="royalties"
        loading={loading}
        lastSync={lastSync}
        totalVolume={totalVolume}
      />
    </main>
  );
}

export default Dashboard;
